import React from 'react';

const HomePage = () => {
  return (
    <div className="container mx-auto px-4">
      <header className="bg-blue-800 text-white text-center p-6">
        <h1 className="text-4xl font-bold">Ramalingam Sridhar</h1>
        <p className="text-xl">Associate Professor, Department of Computer Science and Engineering</p>
        <p className="mt-2">University at Buffalo, The State University of New York</p>
      </header>

      <nav className="flex justify-center space-x-4 bg-gray-200 p-4">
        {['Bio', 'Research/HPVSA Lab', 'Teaching', 'Publications', 'Professional Activities', 'News', 'Resource Links'].map((item) => (
          <a href={`#${item.toLowerCase()}`} className="text-blue-600 hover:text-blue-800">{item}</a>
        ))}
      </nav>

      <main>
        <section id="bio" className="my-8">
          <h2 className="text-3xl font-bold mb-4">Bio</h2>
          <p>A brief bio here...</p>
        </section>
        {/* Repeat for other sections */}
      </main>

     {/* Add more vertical space above the footer, if needed */}
<footer className="mt-10 bg-gray-800 text-white text-center p-6">
  Contact info and other details here...
</footer>

    </div>
  );
};

export default HomePage;
