import React from 'react';
import professorPhoto from './professorPhoto.jpeg'; // Adjust the path as necessary

const HomePage = () => {
  return (
    <div className="container mx-auto px-4 py-8">
      <header className="text-center space-y-4 mb-12">
        <img src={professorPhoto} alt="Ramalingam Sridhar" className="mx-auto rounded-full w-32 h-32 object-cover border-4 border-white shadow-lg" />
        <h1 className="text-4xl font-bold text-gray-800">Ramalingam Sridhar</h1>
        <p className="text-xl text-gray-600">Associate Professor, Department of Computer Science and Engineering</p>
        <p className="text-gray-500">University at Buffalo, The State University of New York</p>
      </header>

      <nav className="flex justify-center space-x-4 bg-blue-500 p-4 rounded-lg shadow-md mb-12">
        {['Bio', 'Research/HPVSA Lab', 'Teaching', 'Publications', 'Professional Activities', 'News', 'Resource Links'].map((item) => (
          <a href={`#${item.toLowerCase()}`} className="text-white hover:underline">{item}</a>
        ))}
      </nav>

      <main>
        <section id="bio" className="prose prose-xl mx-auto mb-12">
          <h2>Bio</h2>
          <p>Professor Ramalingam Sridhar is an esteemed Associate Professor at the University of Buffalo's Department of Computer Science and Engineering, renowned for his pioneering contributions to green computing and energy-aware systems.</p>
          {/* Include the full bio content here */}
        </section>
        {/* Repeat for other sections */}
      </main>

      <footer className="bg-gray-800 text-white text-center p-6 mt-10">
        Contact info and other details here...
      </footer>
    </div>
  );
};

export default HomePage;
